mapper.update:

update image
set external_image_id = #{externalImageId,jdbcType=VARCHAR},
  upload_user_id = #{uploadUserId,jdbcType=BIGINT},
  image_type = #{imageType,jdbcType=VARCHAR},
  image_format = #{imageFormat,jdbcType=VARCHAR}
where image_id = #{imageId,jdbcType=BIGINT}

mapper.select:

select image_id, external_image_id, upload_user_id, image_type, image_format
from image
where image_id = #{imageId,jdbcType=BIGINT}
